installation.script:
#!/bin/bash

mkdir -p /mnt/server
cd /mnt/server

DEDICATED_URL="https://www.unrealsoftware.de/files_pub/cs2d_dedicated_linux.zip"
CLIENT_URL="https://github.com/GGLVXD/FuckCS2D/raw/refs/heads/main/cs2d_1014_linux.zip"

echo "Downloading CS2D dedicated server..."
curl -L -o cs2d_dedicated_linux.zip "${DEDICATED_URL}" || { echo "Failed to download dedicated server."; exit 1; }

echo "Downloading CS2D client..."
curl -L -o cs2d_1014_linux.zip "${CLIENT_URL}" || { echo "Failed to download CS2D client."; exit 1; }

unzipy() {
    local file=$1
    echo "Unzipping ${file}..."

    unzip -o "${file}" || { echo "Failed to unzip ${file}."; exit 1; }
}
unzip "cs2d_dedicated_linux.zip"
unzip "cs2d_1014_linux.zip"

chmod +x ./cs2d_dedicated

echo "deleting zip files..."
rm -f cs2d_dedicated_linux.zip cs2d_1014_linux.zip

echo "Installation completed successfully."
